import React, { Component } from 'react'
import VisibilitySensor from "react-visibility-sensor"
import { TimelineMax } from 'gsap'
import Container from '../Container/Container'
import Dots from '../Dots/Dots'
import PropTypes from 'prop-types'
import style from './Portfolio.module.scss'

class Portfolio extends Component {

  visiblePortfolio = isVisible => {
    var tlBottomMenu = new TimelineMax({})

    if (isVisible) tlBottomMenu.staggerTo(".HeadNav_HeadNav__item__1DohS", .5, {height:0, opacity:0}, 0.2)
    else tlBottomMenu.staggerTo(".HeadNav_HeadNav__item__1DohS", .5, {height:100, opacity:1}, 0.2)
  }

  render() {
    const { title } = this.props

    return (
      <VisibilitySensor
        minTopValue={100}
        partialVisibility='top'
        onChange={this.visiblePortfolio}
      >
            <section className={style.Portfolio}>
              <Container>
                <div className={style.Portfolio__wrapper}>
                  <div className={style.Portfolio__infoCol}>
                    <h2 className={style.Portfolio__title}>{ title }</h2>

                    <div className={style.Portfolio__screenDesc}>
                      <Dots>
                        <p className={style.Portfolio__descP}>По этому каждый сайт, 
делаем как для себя.</p>
                      </Dots>
                      <Dots>
                        <p className={style.Portfolio__descP}>По этому каждый сайт, 
делаем как для себя.</p>
                      </Dots>
                    </div>
                  </div>
                  <div className={style.Portfolio__portfolioWr}>Portfolio</div>
                </div>
              </Container>
            </section>
      </VisibilitySensor>
    );
  }
}

Portfolio.propTypes = {

};

export default Portfolio;
