import React, { Component } from 'react'
import HeadScreen from '../../components/HeadScreen/HeadScreen'
import axios from 'axios'
import styles from './Home.module.scss'

export class Home extends Component {
  state = {
    titles: {},
    titlesIsLoaded: false,
    descriptions: {},
    descriptionsIsLoaded: false
  }

  componentDidMount() {
    axios.get(`/wp-json/wp/v2/home_titles?order=asc`)
      .then(res => this.setState({
        titles: res.data.map(title => title.title.rendered),
        titlesIsLoaded: true
      }))
      .catch(err => console.log(err))

    axios.get(`/wp-json/wp/v2/title-descriptions`)
      .then(res => this.setState({
        descriptions: res.data,
        descriptionsIsLoaded: true
      }))
      .catch(err => console.log(err))
  }

  getDescAgency = desc => {
    desc = desc.filter( des => des.id === 35 )
    return desc
  }

  render() {
    const { titles, titlesIsLoaded, descriptions, descriptionsIsLoaded } = this.state
    const descAgancy = descriptions.filter( desc => desc.id === '35' )
    const descAbout = descriptions.filter( desc => desc.id === '36' )

    if (titlesIsLoaded && descriptionsIsLoaded) {
      return (
        <HeadScreen title={titles[0]} descAgancy={descAgancy} descAbout={descAbout} />
      )
    }

    return <h1>Loading...</h1>
  }
}

export default Home
