import React, { Component } from 'react'
import VisibilitySensor from "react-visibility-sensor"
import { TimelineMax } from 'gsap'
import axios from 'axios'
import Container from '../Container/Container'
import Dots from '../Dots/Dots'
import PortfolioItem from '../PortfolioItem/PortfolioItem'
import PropTypes from 'prop-types'
import style from './Portfolio.module.scss'

class Portfolio extends Component {

  state = {
    projects: {},
    firstRowProjects: {},
    secondRowProjects: {},
    projectsIsLoaded: false
  }

  componentDidMount() {
    axios.get(`/wp-json/wp/v2/projects`)
      .then(res => this.getProjectToHome(res.data))
      .catch(err => console.log(err))
  }

  getProjectToHome = projects => {
    const homePageProject = projects.filter(project => project.acf.project_show_in_home)

    this.setState({
      projects: homePageProject,
      projectsIsLoaded: true
    })

    const firstRowProjects = homePageProject.filter((e,i) => !(i%2) ? e : null)
    const secondRowProjects = homePageProject.filter((e,i) => (i%2))

    console.log(this.state)
  }

  visiblePortfolio = isVisible => {
    var tlBottomMenu = new TimelineMax({})

    if (isVisible) tlBottomMenu.staggerTo(".HeadNav_HeadNav__item__1DohS", .5, {height:0, opacity:0}, 0.2)
    else tlBottomMenu.staggerTo(".HeadNav_HeadNav__item__1DohS", .5, {height:100, opacity:1}, 0.2)
  }

  render() {
    const { title, firstDesc, secondDesc } = this.props

    return (
      <VisibilitySensor
        minTopValue={100}
        partialVisibility='top'
        onChange={this.visiblePortfolio}
      >
            <section className={style.Portfolio}>
              <Container>
                <div className={style.Portfolio__wrapper}>
                  <div className={style.Portfolio__infoCol}>
                    <h2 className={style.Portfolio__title}>{ title }</h2>

                    <div className={style.Portfolio__screenDesc}>
                      <Dots>
                        <p className={style.Portfolio__descP}>{ firstDesc }</p>
                      </Dots>
                      <Dots>
                        <p className={style.Portfolio__descP}>{ secondDesc }</p>
                      </Dots>
                    </div>
                  </div>
                  <div className={style.Portfolio__portfolioWr}>
                    <div className={style.Portfolio__portfolioRow}>
                      <PortfolioItem/>
                    </div>
                    <div className={style.Portfolio__portfolioRow}>
                      <PortfolioItem/>
                    </div>
                  </div>
                </div>
              </Container>
            </section>
      </VisibilitySensor>
    );
  }
}

Portfolio.propTypes = {
  title: PropTypes.string.isRequired,
  firstDesc: PropTypes.string.isRequired,
  secondDesc: PropTypes.string.isRequired
};

export default Portfolio;
